// Dynamically import the Leaflet components to avoid SSR issues
const MapContainer = dynamic(
  () => import('react-leaflet').then((mod) => mod.MapContainer),
  { ssr: false }
);

const TileLayer = dynamic(
  () => import('react-leaflet').then((mod) => mod.TileLayer),
  { ssr: false }
);

const CircleMarker = dynamic(
  () => import('react-leaflet').then((mod) => mod.CircleMarker),
  { ssr: false }
);

const Popup = dynamic(
  () => import('react-leaflet').then((mod) => mod.Popup),
  { ssr: false }
);

export const Map = forwardRef<MapRef, MapProps>((props, ref) => {
  const selectedDate = props.selectedDate || { year: 2024, month: 1 };
  const [mapReady, setMapReady] = useState(false);
  const [currentDate, setCurrentDate] = useState(selectedDate);

  // Load Leaflet CSS dynamically on the client side
  useEffect(() => {
    // Only run on client side
    if (typeof window !== 'undefined') {
      const link = document.createElement('link');
      link.rel = 'stylesheet';
      link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
      link.integrity = 'sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY=';
      link.crossOrigin = '';
      document.head.appendChild(link);
    }
  }, []);

  useEffect(() => {
    // Set a small delay to ensure the component is mounted before rendering the map
    // This helps prevent SSR issues with Leaflet
    const timer = setTimeout(() => {
      setMapReady(true);
    }, 100);
    
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    setCurrentDate(selectedDate);
  }, [selectedDate]);

  const updateDate = (date: { year: number; month: number }) => {
    setCurrentDate(date);
  };

  const resetMap = () => {
    setCurrentDate({ year: 2024, month: 1 });
  };

  useImperativeHandle(ref, () => ({
    updateDate,
    resetMap
  }));

  // Calculate total months since Jan 2024 (for current date and hotspot dates)
  const currentTotalMonths = (currentDate.year - 2024) * 12 + (currentDate.month - 1);
  
  // Filter hotspots based on the current date
  const visibleHotspots = HOTSPOTS.filter(hotspot => {
    const hotspotTotalMonths = (hotspot.firstYear - 2024) * 12 + (hotspot.firstMonth - 1);
    return hotspotTotalMonths <= currentTotalMonths;
  });
  
  // Calculate infection statistics based on months passed
  const infectionRate = Math.min(5 + currentTotalMonths * 0.25, 95);
  const mortalityRate = Math.min(1 + currentTotalMonths * 0.04, 25);
  const maskAvailability = Math.max(80 - currentTotalMonths * 0.33, 20);
  
  // Get phase description based on date
  const getPhaseDescription = () => {
    const { year, month } = currentDate;
  
    if (year === 2024 && month <= 3) {
      return "Initial outbreak detected in Florida. Officials underestimate the risk, mistaking symptoms for seasonal allergies.";
    } else if (year === 2024 && month <= 6) {
      return "Early southern spread. Public health centers report rising respiratory cases. First testing centers are deployed.";
    } else if (year === 2024 && month <= 9) {
      return "Authorities link the illness to mutated pollen. Climate researchers warn of warming-related pollen surges.";
    } else if (year === 2024 && month <= 12) {
      return "P-25 virus officially named and classified. First travel advisories issued for southern states.";
    } else if (year === 2025 && month <= 3) {
      return "Virus spreads to major transportation hubs. First international cases reported. Mask shortages begin.";
    } else if (year === 2025 && month <= 6) {
      return "Global health emergency declared. Research into high-tech masks and treatments accelerates.";
    } else if (year === 2025 && month <= 9) {
      return "First-generation high-tech masks enter production. Infection rates continue to climb globally.";
    } else if (year === 2025 && month <= 12) {
      return "Seasonal pattern emerges: P-25 intensifies in spring and summer. Winter brings temporary relief.";
    } else if (year === 2026 && month <= 6) {
      return "Second wave begins. Virus shows signs of mutation. Vaccine research faces setbacks.";
    } else if (year === 2026 && month <= 12) {
      return "Improved high-tech masks become widely available. Economic impact deepens as industries adapt.";
    } else if (year === 2027 && month <= 6) {
      return "Breakthrough in understanding P-25's mechanism of action. New treatment protocols reduce mortality.";
    } else if (year === 2027 && month <= 12) {
      return "Global cooperation on P-25 research intensifies. First successful clinical trials for treatments.";
    } else if (year === 2028 && month <= 6) {
      return "Virus becomes endemic in most regions. Society adapts with new health protocols and technologies.";
    } else if (year === 2028 && month <= 12) {
      return "First-generation vaccines show limited effectiveness. Research continues for better solutions.";
    } else if (year === 2029 && month <= 6) {
      return "Improved vaccines enter trials. Long-term health impacts of P-25 begin to be understood.";
    } else if (year === 2029 && month <= 12) {
      return "Gradual decline in mortality rates due to improved treatments and adaptive immunity.";
    } else if (year === 2030) {
      return "The world adapts to living with P-25. Scientists caution that the virus may never be fully eradicated.";
    } else {
      return "The world adapts to living with P-25. Scientists caution that the virus may never be fully eradicated.";
    }
  };

  return (
    <div className="relative flex h-[650px] flex-col rounded-xl bg-muted/50 p-4">
      <div className="absolute top-5 right-5 bg-background/80 px-3 py-1 rounded-md text-sm font-medium z-[5]">
        Date: {MONTH_NAMES[currentDate.month - 1]} {currentDate.year}
      </div>
      
      {/* Information panel */}
      <div className="absolute top-5 left-5 bg-background/80 p-3 rounded-md max-w-xs z-[5]">
        <h3 className="text-sm font-bold mb-2">P-25 Status: {MONTH_NAMES[currentDate.month - 1]} {currentDate.year}</h3>
        <p className="text-xs">{getPhaseDescription()}</p>
        
      </div>
      
      {/* Legend */}
      <div className="absolute bottom-5 left-5 bg-background/80 p-3 rounded-md z-[5]">
        <h3 className="text-sm font-bold mb-2">P-25 Virus Spread</h3>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded-full bg-red-500/50"></div>
          <span className="text-xs">Infection Hotspot</span>
        </div>
        <div className="mt-2 text-xs text-muted-foreground">
          First detected in Florida in 2024
        </div>
        <div className="mt-1 text-xs text-muted-foreground">
          Active hotspots: {visibleHotspots.length}
        </div>
      </div>
      
      {/* Map container */}
      <div className="flex-1 w-full h-full relative z-[1]">
        {mapReady && (
          <MapContainer 
            center={[39.8283, -98.5795]} // Center of the US
            zoom={4} 
            style={{ height: '100%', width: '100%', borderRadius: '0.5rem' }}
            zoomControl={false}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            
            {visibleHotspots.map((hotspot, index) => {
              // Calculate the radius based on months since appearance
              const hotspotTotalMonths = (hotspot.firstYear - 2024) * 12 + (hotspot.firstMonth - 1);
              const monthsSinceAppearance = currentTotalMonths - hotspotTotalMonths;
              const growthFactor = Math.min(monthsSinceAppearance * 0.04, 3) + 1;
              const radius = hotspot.radius * growthFactor * (hotspot.intensity * 0.5);
              
              return (
                <CircleMarker
                  key={index}
                  center={[hotspot.lat, hotspot.lng]}
                  radius={radius / 10} // Scale down for map display
                  pathOptions={{
                    fillColor: '#ef4444',
                    fillOpacity: 0.5,
                    color: '#b91c1c',
                    weight: 1
                  }}
                >
                  <Popup>
                    <div className="text-sm">
                      <strong>{hotspot.name}, {hotspot.state}</strong><br/>
                      Infected since: {MONTH_NAMES[hotspot.firstMonth - 1]} {hotspot.firstYear}<br/>
                      Severity: {Math.min(monthsSinceAppearance * 0.8 + 10, 100).toFixed(1)}%
                    </div>
                  </Popup>
                </CircleMarker>
              );
            })}
          </MapContainer>
        )}
      </div>
    </div>
  );
});

Map.displayName = "Map";